// src/components/ContactForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
   const [formData, setFormData] = useState({
      topic: '',
      name: '',
      email: '',
      message: ''
   });

   const [successMessage, setSuccessMessage] = useState('');
   const [errorMessage, setErrorMessage] = useState('');

   const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({
         ...formData,
         [name]: value
      });
   };

   const handleSubmit = async (e) => {
      e.preventDefault();
      try {
         const response = await axios.post('http://localhost:5000/api/contact', formData);
         setSuccessMessage(response.data.message);
         setErrorMessage('');
      } catch (error) {
         setErrorMessage('Error submitting the form. Please try again.');
         setSuccessMessage('');
      }
   };

   return (
      <section className="contact-section">
         <h2>Contact Us, We're Ready to Help!</h2>
         <p>We strive to provide you with the best experience and the best platform to find your choice. Post us any queries and weâ€™ll get back to you.</p>
         
         <form onSubmit={handleSubmit}>
            <label>
               Topic:
               <select name="topic" value={formData.topic} onChange={handleChange} required>
                  <option value="">Select your query topic</option>
                  <option value="support">Rental House</option>
                  <option value="support">Rental Flat</option>
                  <option value="general">Pg</option>
                  <option value="support">Go Downs</option>
               </select>
            </label>

            <label>
               Name:
               <input type="text" name="name" value={formData.name} onChange={handleChange} required />
            </label>

            <label>
               Email:
               <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </label>

            <label>
               Message:
               <textarea name="message" value={formData.message} onChange={handleChange} required></textarea>
            </label>

            <button type="submit">Submit</button>
         </form>

         {successMessage && <p className="success-message">{successMessage}</p>}
         {errorMessage && <p className="error-message">{errorMessage}</p>}
      </section>
   );
};

export default ContactForm;
