// src/components/ContactForm.js
import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
    const [formData, setFormData] = useState({
        topic: '',
        name: '',
        email: '',
        message: ''
    });

    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // Validation function
    const validateForm = () => {
        const errors = {};
        if (!formData.topic) errors.topic = 'Please select a topic';
        if (!formData.name.trim()) errors.name = 'Name is required';
        if (!formData.email) {
            errors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            errors.email = 'Email is invalid';
        }
        if (!formData.message || formData.message.length < 10) {
            errors.message = 'Message must be at least 10 characters';
        }
        return errors;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formErrors = validateForm();
        if (Object.keys(formErrors).length > 0) {
            setErrors(formErrors);
            return;
        }

        try {
            const response = await axios.post('http://localhost:5000/api/contact', formData);
            setSuccessMessage(response.data.message);
            setErrorMessage('');
            setErrors({});
        } catch (error) {
            setErrorMessage('Error submitting the form. Please try again.');
            setSuccessMessage('');
        }
    };

    return (
        <section className="contact-section">
            <h2>Contact Us, We're Ready to Help!</h2>
            <p>We strive to provide you with the best experience and the best platform to find your choice. Post us any queries and weâ€™ll get back to you.</p>
            
            <form onSubmit={handleSubmit}>
                <label>
                    Topic:
                    <select name="topic" value={formData.topic} onChange={handleChange}>
                        <option value="">Select your query topic</option>
                        <option value="general">General Inquiry</option>
                        <option value="support">Customer Support</option>
                    </select>
                    {errors.topic && <p className="error-message">{errors.topic}</p>}
                </label>

                <label>
                    Name:
                    <input type="text" name="name" value={formData.name} onChange={handleChange} />
                    {errors.name && <p className="error-message">{errors.name}</p>}
                </label>

                <label>
                    Email:
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                    {errors.email && <p className="error-message">{errors.email}</p>}
                </label>

                <label>
                    Message:
                    <textarea name="message" value={formData.message} onChange={handleChange} />
                    {errors.message && <p className="error-message">{errors.message}</p>}
                </label>

                <button type="submit">Submit</button>
            </form>

            {successMessage && <p className="success-message">{successMessage}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}
        </section>
    );
};

export default ContactForm;
