// src/components/ContactForm.js
import React, { useState } from 'react';

const ContactForm = () => {
    const [formData, setFormData] = useState({
        topic: '',
        name: '',
        email: '',
        message: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form data:', formData);
    };

    return (
        <section className="contact-section">
            <h2>Contact Us, We're Ready to Help!</h2>
            <p>We strive to provide you with the best experience and the best platform to find your choice. Post us any queries and weâ€™ll get back to you.</p>
            
            <div className="chat-call-section">
                <div className="chat-call-box">
                    <h3>Chat with us !!</h3>
                    <p>Our friendly team is here to help</p>
                    <a href="mailto:to_let@gmail.com">to_let@gmail.com</a>
                </div>
                <div className="chat-call-box">
                    <h3>Call us...</h3>
                    <p>Mon - Fri 8 am to 10 pm</p>
                    <a href="tel:+919876543210">+91 9876543210</a>
                </div>
            </div>

            <form onSubmit={handleSubmit}>
                <label>
                    Topic:
                    <select name="topic" value={formData.topic} onChange={handleChange} required>
                        <option value="">Select your query topic</option>
                        <option value="general">Rental</option>
                        <option value="support">Customer Support</option>
                    </select>
                </label>

                <label>
                    Name:
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </label>

                <label>
                    Email:
                    <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                </label>

                <label>
                    Message:
                    <textarea name="message" value={formData.message} onChange={handleChange} required></textarea>
                </label>

                <button type="submit">Submit</button>
            </form>
        </section>
    );
};

export default ContactForm;
